#include <gtest/gtest.h>
#include "encoding/base64.h"
#include "big_number.h"

TEST(base64, decode_1) {
	constexpr std::uint8_t result[] = "hello world";
	EXPECT_EQ(encoding::base64::from("aGVsbG8gd29ybGQ="), byte_string_view{result});
}

TEST(base64, decode_2) {
	EXPECT_EQ(
		encoding::base64::from(""),
		big_unsigned{""}.to_bytestring(std::endian::big));
}

int main() {
	::testing::InitGoogleTest();
	return RUN_ALL_TESTS();
}
