#include <gtest/gtest.h>
#include "number/fixed.h"
#include "number/flexible.h"

using namespace leaf;

constexpr fixed_unsigned ffdhe2048_p{""};
const auto
		v_ubig_1 = var_unsigned::from_hex(""),
		v_ubig_2 = var_unsigned::from_hex("c953ef9abb3009243f23c6098a9569700a1700b718010c510"),
		v_ubig_3 = var_unsigned::from_hex(""),
		v_ubig_4 = var_unsigned::from_hex("ffffffffffffffffffffffffffffffff31A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE4741");
const auto
		v_u_78 = var_unsigned::from_hex("78"),
		v_u_91 = var_unsigned::from_hex("91");

TEST(var_unsigned, add) {
	EXPECT_EQ(v_u_78 + var_unsigned::from_hex("1"), var_unsigned::from_hex("79"));
	EXPECT_EQ(
			var_unsigned::from_hex("200000000000000") + var_unsigned::from_hex("200000000000000"),
			var_unsigned::from_hex("400000000000000"));
}

TEST(fixed_signed, add) {
	EXPECT_EQ(fixed_signed(-1) + fixed_signed(-2), fixed_signed(-3));
	EXPECT_EQ(fixed_signed(-1) + fixed_signed(2), fixed_signed(1));
}

TEST(fixed_unsigned, minus) {
	EXPECT_EQ(fixed_unsigned("78") - fixed_unsigned("1"), fixed_unsigned("77"));
	EXPECT_EQ(fixed_unsigned("200000000000000") - fixed_unsigned("120000000000000"), fixed_unsigned("e0000000000000"));
	EXPECT_EQ(
			fixed_unsigned("")
			-
			ffdhe2048_p
	,
			fixed_unsigned(""));
}

TEST(var_unsigned, minus) {
	EXPECT_EQ(v_u_78 - var_unsigned::from_hex("1"), var_unsigned::from_hex("77"));
	EXPECT_EQ(
			var_unsigned::from_hex("200000000000000") - var_unsigned::from_hex("120000000000000"),
			var_unsigned::from_hex("e0000000000000"));
	EXPECT_EQ(
			v_ubig_1 - v_ubig_3,
			var_unsigned::from_hex(""));
}

TEST(fixed_signed, minus) {
	EXPECT_EQ(fixed_signed(-1) - fixed_signed(-2), fixed_signed(1));
	EXPECT_EQ(fixed_signed(-1) - fixed_signed(2), fixed_signed(-3));
}

TEST(fixed_unsigned, multiply) {
	EXPECT_EQ(fixed_unsigned("78") * fixed_unsigned("2"), fixed_unsigned("f0"));
	EXPECT_EQ(fixed_unsigned("ff") * fixed_unsigned("ff"), fixed_unsigned("fe01"));
	EXPECT_EQ(fixed_unsigned("200000000000000") * fixed_unsigned("120000000000000"), fixed_unsigned("24000000000000000000000000000"));
	fixed_unsigned n("c953ef9abb3009243f23c6098a9569700a1700b718010c510");
	fixed_unsigned p("7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffed");
	EXPECT_EQ(
			n * n,
			fixed_unsigned("9e54e9c63889168a4863020c8ac813008eb9f6a437a30e7e5d41e60714dd27fe7965dc36f61620f9de4dce1e4939b1a100"));
	EXPECT_EQ(
			n * n % p,
			fixed_unsigned("39f6a437a30e7e5d41e60714dd2815fa008fa35a6f79809d001bfae2ec83b627"));
}

TEST(var_unsigned, multiply) {
	EXPECT_EQ(v_u_78 * var_unsigned::from_hex("2"), var_unsigned::from_hex("f0"));
	EXPECT_EQ(var_unsigned::from_hex("ff") * var_unsigned::from_hex("ff"), var_unsigned::from_hex("fe01"));
	EXPECT_EQ(
			var_unsigned::from_hex("200000000000000") * var_unsigned::from_hex("120000000000000"),
			var_unsigned::from_hex("24000000000000000000000000000"));
	EXPECT_EQ(
			v_ubig_2 * v_ubig_2,
			var_unsigned::from_hex("9e54e9c63889168a4863020c8ac813008eb9f6a437a30e7e5d41e60714dd27fe7965dc36f61620f9de4dce1e4939b1a100"));
}

TEST(fixed_signed, multiply) {
	fixed_signed neg_1(-1), neg_2(-2), _2(2);
	EXPECT_EQ(fixed_signed(-0x100000001) * fixed_signed(-0x100000001), fixed_signed("10000000200000001"));
	EXPECT_EQ(neg_1 * neg_2, _2);
	EXPECT_EQ(fixed_signed<32>(neg_1 * _2), neg_2);

	fixed_signed n("cf24f849a73a7ea45c8515df741d96dc3810050f2ca6dffd3e147b04de09239f");
	fixed_signed r("a79ccedde1f66a738dd14a6434bdb0253dff84a4e4456cce677de7f07eb79c01490d2999b9cd1d5c2d92862e622ca2c50cd50a6773c036690defc1134a22dcc1");
	fixed_signed p("7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffed");
	EXPECT_EQ(n * n, r);
}

TEST(fixed_unsigned, left_shift) {
	fixed_unsigned x150(0x150), x54(0x54), x0(0);
	fixed_unsigned x15e33("15000000000000000000000000000000000");
	EXPECT_EQ(x54 << 0, x54);
	EXPECT_EQ(x54 << sizeof(std::uint64_t) * 8, x0);
	EXPECT_EQ(x15e33 << 0, x15e33);
	EXPECT_EQ(x54 << 0x2, x150);
	EXPECT_EQ(fixed_unsigned<0x89>(0x54) << 0x82, x15e33);
}

TEST(var_unsigned, left_shift) {
	const auto
			x150 = var_unsigned::from_number(0x150),
			x54 = var_unsigned::from_number(0x54),
			x0 = var_unsigned::from_number(0),
			x15e33 = var_unsigned::from_hex("15000000000000000000000000000000000");
	EXPECT_EQ(x54 << 0, x54);
	EXPECT_EQ(x54 << sizeof(std::uint64_t) * 8, x0);
	EXPECT_EQ(x15e33 << 0, x15e33);
	EXPECT_EQ(x54 << 0x2, x150);
	auto a = var_unsigned::from_number(0x54);
	a.resize(0x89);
	EXPECT_EQ(a << 0x82, x15e33);
}

TEST(fixed_unsigned, right_shift) {
	fixed_unsigned x150(0x150), x54(0x54), x0(0);
	EXPECT_EQ(x54 >> 0, x54);
	EXPECT_EQ(x54 >> sizeof(std::uint64_t) * 8, x0);
	EXPECT_EQ(x54 >> 0x2, fixed_unsigned(0x15));
	EXPECT_EQ(fixed_unsigned("15000000000000000000000000000000000") >> 0x82, x54);
	EXPECT_EQ(
			fixed_unsigned("15000000000000000000000000000000000") >> 64,
			fixed_unsigned("1500000000000000000")
	);
}

TEST(fixed_unsigned, compare) {
	EXPECT_GT(fixed_unsigned("33"), fixed_unsigned("32"));
	EXPECT_GE(fixed_unsigned("33"), fixed_unsigned("32"));
	EXPECT_GE(fixed_unsigned("33"), fixed_unsigned("33"));
}

TEST(fixed_unsigned, modulo) {
	fixed_unsigned x91(0x91);
	EXPECT_EQ(fixed_unsigned(0x1243) % x91, fixed_unsigned(0x23));
	EXPECT_EQ(fixed_unsigned(0x1) % x91, fixed_unsigned(1));
	EXPECT_EQ(x91 % x91, fixed_unsigned(0));

	EXPECT_EQ(fixed_unsigned("2344b72002994a6c") % fixed_unsigned("09cc3c85ddda6f53"), fixed_unsigned("5E0018E6909FC73"));
	EXPECT_EQ(
			fixed_unsigned("bc2f2ce36012a58a7c358e43ebb8f360")
			%
			fixed_unsigned("05936dd643532fa1b05a3c644c50039d"),
			fixed_unsigned("42E0444B25981B2C093C55615687C23"));
	EXPECT_EQ(
			fixed_unsigned("a4f55d2616b9edc25ea7b049e37afd580f21e7e913ee038082fabb4e17c02f19")
			%
			fixed_unsigned("84e1146e243366336baed10b3f7b70046b011e126d98254d9f84fef8aaace7a1"),
			fixed_unsigned("201448b7f286878ef2f8df3ea3ff8d53a420c9d6a655de32e375bc556d134778"));
	// 256 bytes
	EXPECT_EQ(
			leaf::fixed_unsigned("")
			%
			leaf::fixed_unsigned(""),
			leaf::fixed_unsigned("")
	);
	// 2048 bytes
	EXPECT_EQ(
			leaf::fixed_unsigned("")
			%
			leaf::fixed_unsigned(""),
			leaf::fixed_unsigned("")
	);
	EXPECT_EQ(
			leaf::fixed_unsigned("")
			%
			ffdhe2048_p,
			leaf::fixed_unsigned("")
	);
	EXPECT_EQ(
			leaf::fixed_unsigned("")
			%
			ffdhe2048_p,
			leaf::fixed_unsigned("")
	);
}

TEST(var_unsigned, modulo_1) {
	EXPECT_EQ(var_unsigned::from_number(0x1243) % v_u_91, var_unsigned::from_number(0x23));
	EXPECT_EQ(var_unsigned::from_number(1) % v_u_91, var_unsigned::from_number(1));
	EXPECT_EQ(v_u_91 % v_u_91, var_unsigned::from_number(0));
	EXPECT_EQ(
			var_unsigned::from_hex("2344b72002994a6c") % var_unsigned::from_hex("09cc3c85ddda6f53"),
			var_unsigned::from_hex("5E0018E6909FC73"));
	EXPECT_EQ(
			var_unsigned::from_hex("bc2f2ce36012a58a7c358e43ebb8f360") % var_unsigned::from_hex("05936dd643532fa1b05a3c644c50039d"),
			var_unsigned::from_hex("42E0444B25981B2C093C55615687C23"));
	EXPECT_EQ(
			v_ubig_2 * v_ubig_2 % var_unsigned::from_hex("7fffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffed"),
			var_unsigned::from_hex("39f6a437a30e7e5d41e60714dd2815fa008fa35a6f79809d001bfae2ec83b627"));
	EXPECT_EQ(
			var_unsigned::from_hex("a4f55d2616b9edc25ea7b049e37afd580f21e7e913ee038082fabb4e17c02f19") % var_unsigned::from_hex("84e1146e243366336baed10b3f7b70046b011e126d98254d9f84fef8aaace7a1"),
			var_unsigned::from_hex("201448b7f286878ef2f8df3ea3ff8d53a420c9d6a655de32e375bc556d134778"));
}

TEST(var_unsigned, modulo_2) {
	// 256 bytes
	const auto
			a = var_unsigned::from_hex(""),
			b = var_unsigned::from_hex(""),
			c = var_unsigned::from_hex("");
	EXPECT_EQ(a % b, c);
}

TEST(var_unsigned, modulo_3) {
	// 2048 bytes
	const auto
			a = var_unsigned::from_hex(""),
			b = var_unsigned::from_hex(""),
			c = var_unsigned::from_hex("");
	EXPECT_EQ(a % b, c);
}

TEST(var_unsigned, modulo_4) {
	const auto
			a = var_unsigned::from_hex(""),
			b = var_unsigned::from_hex("");
	EXPECT_EQ(a % v_ubig_3, b);
}

TEST(var_unsigned, modulo_5) {
	const auto
			a = var_unsigned::from_hex(""),
			b = var_unsigned::from_hex("");
	EXPECT_EQ(a % v_ubig_3, b);
}

TEST(fixed_signed, modulo) {
	fixed_signed x91(0x91);
	EXPECT_EQ(fixed_signed(0x1234) % x91, fixed_signed(0x14));
	EXPECT_EQ(fixed_signed(0x1) % x91, fixed_signed(0x1));
	EXPECT_EQ(x91 % x91, fixed_signed(0));
}

TEST(fixed_unsigned, exp_mod) {
	EXPECT_EQ(exp_mod(fixed_unsigned(2), fixed_unsigned(3), fixed_unsigned(7)), fixed_unsigned(1));
	EXPECT_EQ(
			exp_mod(
					fixed_unsigned(2),
					fixed_unsigned("ffffffffffffffffffffffffffffffff31A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE4741"),
					fixed_unsigned(689)
			),
			fixed_unsigned(0xd6));
	EXPECT_EQ(exp_mod(fixed_unsigned("2"), fixed_unsigned("41"), ffdhe2048_p ), fixed_unsigned("20000000000000000"));
	EXPECT_EQ(
			exp_mod(fixed_unsigned("2"), fixed_unsigned("fff"), ffdhe2048_p),
			fixed_unsigned(""));
	EXPECT_EQ(
			exp_mod(fixed_unsigned("2"), fixed_unsigned("ffffffffffffffffffffffffffffffff"), ffdhe2048_p),
			fixed_unsigned("")
	);
	EXPECT_EQ(
			exp_mod(
					fixed_unsigned("2"),
					fixed_unsigned("ffffffffffffffffffffffffffffffff31A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE474131A07D962A192A85702CDDB75EBE4741"),
					ffdhe2048_p
			),
			fixed_unsigned("")
	);
}

TEST(var_unsigned, exp_mod) {
	EXPECT_EQ(
			exp_mod(var_unsigned::from_number(2), var_unsigned::from_number(3), var_unsigned::from_number(7)),
			var_unsigned::from_number(1));
	EXPECT_EQ(
			exp_mod(var_unsigned::from_number(2), v_ubig_4, var_unsigned::from_number(689)),
			var_unsigned::from_number(0xd6));
	EXPECT_EQ(
			exp_mod(var_unsigned::from_number(2), var_unsigned::from_number(0x41), v_ubig_3),
			var_unsigned::from_hex("20000000000000000"));
	EXPECT_EQ(
			exp_mod(var_unsigned::from_number(2), var_unsigned::from_number(0xfff), v_ubig_3),
			var_unsigned::from_hex(""));
	EXPECT_EQ(
			exp_mod(var_unsigned::from_number(2), var_unsigned::from_hex("ffffffffffffffffffffffffffffffff"), v_ubig_3),
			var_unsigned::from_hex(""));
	EXPECT_EQ(
			exp_mod(var_unsigned::from_number(2), v_ubig_4, v_ubig_3),
			var_unsigned::from_hex(""));
}

TEST(fixed_unsigned, set) {
	fixed_unsigned first("fedcba9876543210");
	fixed_unsigned<16> second(0xabcd);
	first.set(second);
	EXPECT_EQ(first, fixed_unsigned("fedcba987654abcd"));
	auto n = fixed_unsigned<449>(1) << 448;
	EXPECT_TRUE(n.bit(448));
}

